import React from 'react';
import ReactDOM from 'react-dom/client';
import { useState } from 'react';

function FavoriteColor() {
    const [inputValue, setInputValue] = useState('');
    const textcolor={color:'red',};
    const [inputValues, setInputValues] = useState(['item 1','item 2','item 3']);
    const handleInputChange = (event) => {

        setInputValue(event.target.value); // Update the state with the input value

      };

    const handleNewComments = ()=> {
        alert({inputValue});
        setInputValues((prevItems) => [...prevItems, inputValue]);
    }  

    return (
      <div>
        {inputValues.map((x, index) => (
          <h1 key={index} style={textcolor}>{x}</h1>
        ))}

        <form>
            <label>Enter your comments:<br/>
            <textarea cols='40' rows='2'
            onChange={handleNewComments}
            />
            </label>
        </form>
        <button
          type="button"
          onClick={handleNewComments}
        >Submit</button>
      </div>
    );
  }

const root = ReactDOM.createRoot(document.getElementById('root'));
alert(typeof(root));
root.render(<FavoriteColor />);
